std::string markdown_to_html(const std::string& markdown_text) {
    // Convert markdown bold to html
    std::regex bold_pattern("\\*([^\\*]+?)\\*");
    std::string html_text = std::regex_replace(markdown_text, bold_pattern, "<b>$1</b>");

    // Convert markdown italic to html
    std::regex italic_pattern("\\*([^\\*]+?)\\*");
    html_text = std::regex_replace(html_text, italic_pattern, "<i>$1</i>");

    // Convert markdown headers to html
    std::regex header_pattern("^([^#]+?)#*");
    std::string header_text = std::regex_replace(markdown_text, header_pattern, "<h1>$1</h1>");
    html_text.insert(0, header_text);

    return html_text;
}

