#include "snake.h"
#include "SDL.h"

int main(int argc, char* argv[]) {
   SDL_Init(SDL_INIT_VIDEO);
   SDL_Window *window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_CENTERED, SDL_WINDOWPOS_CENTERED, WINDOW_WIDTH, WINDOW_HEIGHT, SDL_WINDOW_SHOWN);
   SDL_Renderer *renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);

   struct Snake *snake = malloc(sizeof(struct Snake));
   SDL_Texture *headTexture = SDL_CreateTextureFromSurface(renderer, IMG_LoadTexture(renderer, "snakeHead.png"));
   SDL_Texture *bodyTexture = SDL_CreateTextureFromSurface(renderer, IMG_LoadTexture(renderer, "snakeBody.png"));
   SDL_Texture *foodTexture = SDL_CreateTextureFromSurface(renderer, IMG_LoadTexture(renderer, "food.png"));

   SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
   SDL_RenderFillRect(renderer, &window);

   SDL_Event event;

   while (1) {
      while (SDL_PollEvent(&event)) {
         if (event.type == SDL_QUIT) {
            printf("Game Over!\n");
            exit(0);
         }
         if (event.type == SDL_KEYDOWN) {
            if (event.key.keysym.sym == SDLK_UP) {
               snake->direction = UP;
            } else if (event.key.keysym.sym == SDLK_DOWN) {
               snake->direction = DOWN;
            } else if (event.key.keysym.sym == SDLK_LEFT) {
               snake->direction = LEFT;
            } else if (event.key.keysym.sym == SDLK_RIGHT) {
               snake->direction = RIGHT;
            }
         }
      }

      moveSnake(snake);

      SDL_SetRenderDrawColor(renderer, 0, 0, 0, 255);
      SDL_RenderFillRect(renderer, &window);

      SDL_Rect headRect = {snake->head.x, snake->head.y, 10, 10};
      SDL_RenderCopy(renderer, headTexture, NULL, &headRect);
      for (int i = 0; i < MAX_BODY_LENGTH; i++) {
         SDL_Rect bodyRect = {snake->body[i].x, snake->body[i].y, 10, 10};
         SDL_RenderCopy(renderer, bodyTexture, NULL, &bodyRect);
      }
      SDL_Rect foodRect = {food.x, food.y, 10, 10};
      SDL_RenderCopy(renderer, foodTexture, NULL, &foodRect);

      SDL_RenderPresent(renderer);

      SDL_Delay(100);
   }

   SDL_DestroyRenderer(renderer);
   SDL_DestroyWindow(window);
   SDL_Quit();

   free(snake);

   return 0;
}
```

