def longestCommonSubsequence(str1, str2):
    # Create a 2D array to store the lengths of common subsequences
    dp = [[0 for x in range(len(str2) + 1)] for x in range(len(str1) + 1)]

    # Iterate through both strings
    for i in range(1, len(str1) + 1):
        for j in range(1, len(str2) + 1):
            # If the current characters match, then the length of the common subsequence is one more
            if str1[i-1] == str2[j-1]:
                dp[i][j] = dp[i-1][j-1] + 1
            # Otherwise, the length of the common subsequence is the maximum of the lengths without the current characters
            else:
                dp[i][j] = max(dp[i-1][j], dp[i][j-1])

    # Reconstruct the longest common subsequence from the 2D array
    lcs = ''
    i, j = len(str1), len(str2)
    while i > 0 and j > 0:
        # If the current characters match, then add the character to the subsequence and move diagonally
        if str1[i-1] == str2[j-1]:
            lcs = str1[i-1] + lcs
            i -= 1
            j -= 1
        # Otherwise, move in the direction of the maximum length
        elif dp[i-1][j] > dp[i][j-1]:
            i -= 1
        else:
            j -= 1

    return lcs

